mapper.update:
update captain
cap_idysn = 'Y', cap_licenseyn = 'Y', cap_manager =#{cap_manager}, cap_cancelreason ='없음'
where u_userid=#{u_userid}

update captain

	cap_idysn = 'N',
	cap_licenseyn = 'N',
	cap_cancelreason =#{cap_cancelreason},	
	cap_manager =#{cap_manager}				

 where u_userid=#{u_userid}
